 &lt;script type=&quot;text/javascript&quot;&gt;
 $(function() {
var fadeSpeed = ($.browser.safari ? 600 : 450);
$(&#039;#logotype&#039;).append(&#039;&lt;span class=&quot;hover&quot;&gt;&lt;/span&gt;&#039;).addClass(&#039;noHover&#039;);
$(&#039;.hover&#039;).css(&#039;opacity&#039;, 0);
$(&#039;.hover&#039;).parent().hover(function() {
$(&#039;.hover&#039;, this).stop().animate({
&#039;opacity&#039;: 1
},
fadeSpeed)
},
function() {
$(&#039;.hover&#039;, this).stop().animate({
&#039;opacity&#039;: 0
},
fadeSpeed)
});
});
&lt;/script&gt;
